package fxClasses;import javafx.application.Application;import javafx.fxml.FXMLLoader;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.image.Image;import javafx.stage.Stage;/** * * * * @author Ali * */public class TestverktygDesktopClient extends Application{    @Override    public void start(Stage stage) throws Exception    {        Parent root = FXMLLoader.load(getClass().getResource("Login.fxml"));        Scene scene = new Scene(root);        stage.setScene(scene);        stage.setResizable(false);        stage.getIcons().add(new Image("/style/orange_brain.jpg"));        stage.setTitle("Newton Testverktyg");        stage.show();    }    /**     *     * @param args the command line arguments     *     */    public static void main(String[] args)    {        launch(args);    }}